"use strict";(self.webpackChunkdb_dev=self.webpackChunkdb_dev||[]).push([[6060],{3396:(e,t,i)=>{i.r(t),i.d(t,{assets:()=>o,contentTitle:()=>a,default:()=>m,frontMatter:()=>r,metadata:()=>l,toc:()=>d});var n=i(7624),s=i(2172);const r={id:"uuid",sidebar_position:4,description:"\u0418\u0441\u043f\u043e\u043b\u044c\u0437\u043e\u0432\u0430\u043d\u0438\u0435 UUID"},a="\u0418\u0441\u043f\u043e\u043b\u044c\u0437\u043e\u0432\u0430\u043d\u0438\u0435 UUID",l={id:"postgresql/uuid",title:"\u0418\u0441\u043f\u043e\u043b\u044c\u0437\u043e\u0432\u0430\u043d\u0438\u0435 UUID",description:"\u0418\u0441\u043f\u043e\u043b\u044c\u0437\u043e\u0432\u0430\u043d\u0438\u0435 UUID",source:"@site/docs/03.postgresql/03.uuid.md",sourceDirName:"03.postgresql",slug:"/postgresql/uuid",permalink:"/db-docs/docs/postgresql/uuid",draft:!1,unlisted:!1,editUrl:"https://github.com/sirius-db-dev/db-docs/tree/main/docs/03.postgresql/03.uuid.md",tags:[],version:"current",sidebarPosition:4,frontMatter:{id:"uuid",sidebar_position:4,description:"\u0418\u0441\u043f\u043e\u043b\u044c\u0437\u043e\u0432\u0430\u043d\u0438\u0435 UUID"},sidebar:"tutorialSidebar",previous:{title:"\u0421\u0432\u044f\u0437\u044c \u043c\u043d\u043e\u0433\u0438\u0435 \u043a\u043e \u043c\u043d\u043e\u0433\u0438\u043c",permalink:"/db-docs/docs/postgresql/relations/many_to_many"}},o={},d=[];function c(e){const t={code:"code",h1:"h1",pre:"pre",...(0,s.M)(),...e.components};return(0,n.jsxs)(n.Fragment,{children:[(0,n.jsx)(t.h1,{id:"\u0438\u0441\u043f\u043e\u043b\u044c\u0437\u043e\u0432\u0430\u043d\u0438\u0435-uuid",children:"\u0418\u0441\u043f\u043e\u043b\u044c\u0437\u043e\u0432\u0430\u043d\u0438\u0435 UUID"}),"\n",(0,n.jsx)(t.pre,{children:(0,n.jsx)(t.code,{className:"language-sql",children:"create extension if not exists \"uuid-ossp\";\n\ndrop table if exists actors, films, film_to_actor cascade;\n\ncreate table actors\n(\n    id         uuid primary key default uuid_generate_v4(),\n    first_name text,\n    last_name  text,\n    birth_date date\n);\n\ncreate table films\n(\n    id          uuid primary key default uuid_generate_v4(),\n    title       text,\n    imdb_rating float,\n    year        int\n);\n\ncreate table film_to_actor\n(\n    actor_id uuid references actors,\n    film_id  uuid references films,\n    primary key (actor_id, film_id)\n);\n\ninsert into actors(first_name, last_name, birth_date)\nvalues ('\u0422\u043e\u043c', '\u0425\u043e\u043b\u043b\u0430\u043d\u0434', '1996-06-01'),\n       ('\u0411\u0435\u043d\u0435\u0434\u0438\u043a\u0442', '\u041a\u0430\u043c\u0431\u0435\u0440\u0431\u044d\u0442\u0447', '1976-07-19'),\n       ('\u041c\u0430\u0440\u043a', '\u0420\u0443\u0444\u0444\u0430\u043b\u043e', '1956-11-22'),\n       ('\u041a\u0440\u0438\u0441', '\u0425\u0435\u043c\u0441\u0432\u043e\u0440\u0442', '1983-08-11'),\n       ('\u0410\u043d\u0430\u0441\u0442\u0430\u0441\u0438\u044f', '\u041f\u0430\u043d\u0438\u043d\u0430', '1983-01-15');\n\ninsert into films(title, imdb_rating, year)\nvalues ('\u0427\u0435\u043b\u043e\u0432\u0435\u043a-\u043f\u0430\u0443\u043a: \u041d\u0435\u0442 \u043f\u0443\u0442\u0438 \u0434\u043e\u043c\u043e\u0439', 8.2, 2021),\n       ('\u041c\u0441\u0442\u0438\u0442\u0435\u043b\u0438: \u0424\u0438\u043d\u0430\u043b', 8.4, 2019),\n       ('\u0422\u043e\u0440: \u0420\u0430\u0433\u043d\u0430\u0440\u0451\u043a', 7.9, 2017),\n       ('\u041a\u043e\u0439\u044f\u0430\u043d\u0438\u0441\u043a\u0430\u0446\u0438', 8.2, 1982);\n\ninsert into film_to_actor(actor_id, film_id)\nvalues\n    ((select id from actors where last_name = '\u0425\u043e\u043b\u043b\u0430\u043d\u0434'),\n     (select id from films where title = '\u0427\u0435\u043b\u043e\u0432\u0435\u043a-\u043f\u0430\u0443\u043a: \u041d\u0435\u0442 \u043f\u0443\u0442\u0438 \u0434\u043e\u043c\u043e\u0439')),\n    ((select id from actors where last_name = '\u0425\u043e\u043b\u043b\u0430\u043d\u0434'),\n     (select id from films where title = '\u041c\u0441\u0442\u0438\u0442\u0435\u043b\u0438: \u0424\u0438\u043d\u0430\u043b')),\n    ((select id from actors where last_name = '\u041a\u0430\u043c\u0431\u0435\u0440\u0431\u044d\u0442\u0447'),\n     (select id from films where title = '\u0427\u0435\u043b\u043e\u0432\u0435\u043a-\u043f\u0430\u0443\u043a: \u041d\u0435\u0442 \u043f\u0443\u0442\u0438 \u0434\u043e\u043c\u043e\u0439')),\n    ((select id from actors where last_name = '\u041a\u0430\u043c\u0431\u0435\u0440\u0431\u044d\u0442\u0447'),\n     (select id from films where title = '\u041c\u0441\u0442\u0438\u0442\u0435\u043b\u0438: \u0424\u0438\u043d\u0430\u043b')),\n    ((select id from actors where last_name = '\u0420\u0443\u0444\u0444\u0430\u043b\u043e'),\n     (select id from films where title = '\u0422\u043e\u0440: \u0420\u0430\u0433\u043d\u0430\u0440\u0451\u043a')),\n    ((select id from actors where last_name = '\u0420\u0443\u0444\u0444\u0430\u043b\u043e'),\n     (select id from films where title = '\u041c\u0441\u0442\u0438\u0442\u0435\u043b\u0438: \u0424\u0438\u043d\u0430\u043b')),\n    ((select id from actors where last_name = '\u0425\u0435\u043c\u0441\u0432\u043e\u0440\u0442'),\n     (select id from films where title = '\u0422\u043e\u0440: \u0420\u0430\u0433\u043d\u0430\u0440\u0451\u043a')),\n    ((select id from actors where last_name = '\u0425\u0435\u043c\u0441\u0432\u043e\u0440\u0442'),\n     (select id from films where title = '\u041c\u0441\u0442\u0438\u0442\u0435\u043b\u0438: \u0424\u0438\u043d\u0430\u043b'));\n\nselect\n  a.id,\n  a.first_name,\n  a.last_name,\n  a.birth_date,\n  coalesce(jsonb_agg(jsonb_build_object(\n    'id', f.id, 'title', f.title, 'imdb_rating', f.imdb_rating))\n      filter (where f.id is not null), '[]') as films\nfrom actors a\nleft join film_to_actor af on a.id = af.actor_id\nleft join films f on f.id = af.film_id\ngroup by a.id;\n"})})]})}function m(e={}){const{wrapper:t}={...(0,s.M)(),...e.components};return t?(0,n.jsx)(t,{...e,children:(0,n.jsx)(c,{...e})}):c(e)}}}]);